mapper.update:
UPDATE `user`
SET pass_word = #{password}
WHERE
    id = #{id}
    AND deleted = FALSE

mapper.select:
SELECT a.user_name userName, a.nick_name nickName, a.mobile, a.created_time createdTime, a.locked, a.lock_time lockTime, a.id, a.address, GROUP_CONCAT(c.`name`) tagName FROM USER a LEFT JOIN user_tag b ON a.id = b.user_id AND a.deleted = FALSE JOIN tag c ON b.tag_id = c.id AND c.deleted = FALSE
AND ( a.address LIKE CONCAT('%',#{param.key}, '%') OR a.user_name LIKE CONCAT('%',#{param.key}, '%') OR a.nick_name LIKE CONCAT('%', #{param.key}, '%') OR a.mobile LIKE CONCAT('%',#{param.key}, '%') OR a.unique_no LIKE CONCAT('%', #{param.key}, '%') )
AND c.id = #{param.tagId}
AND a.locked = #{param.locked}
GROUP BY a.id
